mapper.select:
select * FROM jsh_depotitem where 1=1
and name like '%${name}%'
and type=${type}
and remark like '%${remark}%'
and ifnull(delete_Flag,'0') !='1'
limit #{offset},#{rows}

select di.*,m.Name mName,m.Model mModel from jsh_depotitem di
inner join jsh_material m on di.MaterialId=m.Id  and ifnull(m.delete_Flag,'0') !='1'
where 1=1

    and di.MaterialId=${mId}

and ifnull(di.delete_Flag,'0') !='1'

    limit #{offset},#{rows}

select count(*) from jsh_depotitem where 1=1

    and MaterialId=${mId}

and ifnull(delete_Flag,'0') !='1'

select dep.id,dep.headerId,dep.materialid,dep.munit,dep.opernumber, dep.basicnumber,dep.unitprice,dep.taxunitprice,dep.allprice,dep.remark, dep.img,dep.incidentals,dep.depotid,dep.anotherdepotid,dep.taxrate, dep.taxmoney,dep.taxlastmoney,dep.otherfield1,dep.otherfield2,dep.otherfield3, dep.otherfield4,dep.otherfield5,dep.mtype from jsh_depotitem dep,jsh_material mat where 1=1
and dep.HeaderId = #{depotheadId}
and dep.materialid=mat.id
and mat.enableSerialNumber = #{enableSerialNumber}
and ifnull(dep.delete_Flag,'0') !='1' and ifnull(mat.delete_Flag,'0') !='1'

mapper.delete:
delete from jsh_depotitem
where 1=1
and HeaderId in
(

    #{depotheadId}

)

mapper.update:
update jsh_depotitem set delete_Flag='1' where 1=1 and HeaderId in (
#{depotheadId}
)

update jsh_depotitem
set delete_Flag='1'
where 1=1
and id in (

    #{id}

)
